package com.itwill.dto;

public class Statement {
	private String stmtNo;
	private String stmtDiv;
	private String stmtName;
	private String stmtDate;
	private String stmtMonth;
	
	public Statement() {
		// TODO Auto-generated constructor stub
	}

	public String getStmtNo() {
		return stmtNo;
	}

	public void setStmtNo(String stmtNo) {
		this.stmtNo = stmtNo;
	}

	public String getStmtDiv() {
		return stmtDiv;
	}

	public void setStmtDiv(String stmtDiv) {
		this.stmtDiv = stmtDiv;
	}

	public String getStmtName() {
		return stmtName;
	}

	public void setStmtName(String stmtName) {
		this.stmtName = stmtName;
	}

	public String getStmtDate() {
		return stmtDate;
	}

	public void setStmtDate(String stmtDate) {
		this.stmtDate = stmtDate;
	}

	public String getStmtMonth() {
		return stmtMonth;
	}

	public void setStmtMonth(String stmtMonth) {
		this.stmtMonth = stmtMonth;
	}
	

}
